mapper.select:
select
iduser_idcollection_namecreate_timecontent_countmodule
from collection


        and id = #{id}


        and user_id = #{userId}


        and collection_name = #{collectionName}


        and create_time = #{createTime}


        and content_count = #{contentCount}


        and module = #{module}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from collection


        and id = #{id}


        and user_id = #{userId}


        and collection_name = #{collectionName}


        and create_time = #{createTime}


        and content_count = #{contentCount}


        and module = #{module}

select collection_name,content_count
from collection
where user_id = #{user_id} and module = #{module}

mapper.update:
update collection

    content_count = content_count+1

where user_id = #{userId} and collection_name = #{collectionName} and module = #{module}

update collection

    content_count = content_count-1

where id = #{collectionId}

mapper.delete:
delete
from collection
where user_id = #{userId} and collection_name = #{collectionName} and module = #{module}
